import React from 'react'
import FCRecipesDone from '../FactionalComponenets/FCRecipesDone';
import FCRecpies from '../FactionalComponenets/FCRecpies';



export default class CMyKitchen extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            recipesNotDone: [
                { img: 'https://joyfoodsunshine.com/wp-content/uploads/2016/01/best-chocolate-chip-cookies-recipe-ever-no-chilling-2-e1549147208141.jpg', name: 'Chocolate Chip Cookie', text: 'Amazingly delicious, doughy yet still fully cooked, chocolate chip cookie that turns out perfectly every single time!' }
                , { img: 'https://www.eatthis.com/wp-content/uploads/2019/11/whole-grain-pancake-stack.jpg', name: 'Pancakes with fruits', text: 'SIMPLE. EASY. QUICK. FLUFFY. The Best Fluffy Pancakes recipe you will fall in love with. Full of tips and tricks to help you make the best pancakes…ever!' }
                , { img: 'https://cdn.apartmenttherapy.info/image/upload/v1559194971/k/archive/96a407c135f55b1cbb3c6ad72fd19439c2ca9f76.jpg', name: 'Crème Brûlée', text: 'Five simple ingredients – cream, vanilla, salt, eggs and sugar – make for an exquisitely rich and elegant dessert.' }
            ],
            recipesDone: []
        }
    }

    prepareDish = (index) => {
        let newRecipesDone = [...this.state.recipesDone];
        let newRecipesNotDone = [...this.state.recipesNotDone];
        
        newRecipesDone.push(this.state.recipesNotDone[index]);
        newRecipesNotDone.splice(index, 1);
        this.setState({
            recipesNotDone: newRecipesNotDone,
            recipesDone: newRecipesDone
        })
    }

    eatDish = (index) => {
        this.setState((oldState) => {
            let newRecipesDone = [...oldState.recipesDone];
            let newRecipesNotDone = [...oldState.recipesNotDone];
            newRecipesDone.splice(index, 1);
            newRecipesNotDone.push(oldState.recipesDone[index]);
            return {
                recipesNotDone: newRecipesNotDone,
                recipesDone: newRecipesDone
            }
        })
    }



    render() {
        return (
            <div>
                <h1 style={{textAlign: "center"}}>Desserts Recipes</h1>
                <FCRecpies recipes={this.state.recipesNotDone} onClick={(index) => this.prepareDish(index)} />
                <FCRecipesDone recipes={this.state.recipesDone} onClick={(index) => this.eatDish(index)} />
            </div>
        )
    }

}
