import React from 'react'
import FRecipe from './FCRecipe'
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
    root: {
        display: "flex",
        "justify-content": "center",
        flexFlow: "row",
        marginTop: "7rem",
    },
    media: {
        height: 140,
    },
    text: {
        textAlign: "center",
    }
});

export default function FCRecipesDone(props) {
    const classes = useStyles();
    const needToBeRendered = props.recipes.length > 0;
    if (needToBeRendered) {
        return (
            <div>
                <h1 className={classes.text}>Ready to EAT!</h1>
                <h3 className={classes.text}>Recipes made:{props.recipes.length}</h3>
                <br />
                <div className={classes.root}>
                    {props.recipes.map((rec, index) => (
                        <FRecipe recpie={rec} onClick={props.onClick} key={index} index={index} btnText={"Eat!"} />
                    ))}
                </div>
            </div>
        )
    }
    else {
        return null;
    }

}
