import React from 'react'
import FRecipe from './FCRecipe'
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
  root: {
    display: "flex",
    "justify-content": "center",
    flexFlow: "row",
    marginTop: "7rem",
  },
  media: {
    height: 140,
  },
});

export default function FCRecpies(props) {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      {props.recipes.map((rec, index) => (
        <FRecipe recpie={rec} onClick={props.onClick} key={index} index={index} btnText={"Prepare Dish!"} />
      ))}
    </div>
  )
}
